(function ($) {
	'use strict';
    $(document).on('click', '#account_update', function() {
    	var name = $('#name').val();
    	var address = $('#address').val();
    	var phone = $('#phone').val();
    	var company = $('#company').val();
		if(!name || !address || !phone){
			swal({title: "Đã có lỗi!", text: "Vui lòng điền các thông tin cần thiết để lưu thiết lập", timer: 4000, showConfirmButton: false});
			return false
		}
		var array = [name, address, phone, company];
		swal({title: "Bạn chắc chứ?", text: "Bạn có chắc muốn thực hiện cập nhật thông tin tài khoản?", type: "warning", showCancelButton: true, confirmButtonColor: "#DD6B55", confirmButtonText: "Thực hiện", cancelButtonText: "Để xem lại đã", closeOnConfirm: false},
		function(){
		    $.post('/account/ajax-setting', {data: array}, function(data) {
				var res = $.parseJSON(data);
				if(res.result==='success'){
					swal({title: "Thành công!", text: res.msg, type: "success", timer: 3000, showConfirmButton: false});
				} else {
					swal({title: "Đã có lỗi!", text: res.msg, type: "warning", timer: 4000, showConfirmButton: false});
				}
			});
		});
    });
})(jQuery);