(function($){'use strict';
	function click_login() {
		var e = $.trim($('#email').val());
		var t = $.trim($('#password').val());
		if(!e || !t){swal('Không hợp lệ!','Bạn phải điền địa chỉ email và mật khẩu để đăng nhập','error');return false}
		$('#text').text('Đang đăng nhập...');
		$('#btn-click').prop('disabled', true);
		$.post('/auth/ajaxlogin',{email:e,password:t},function(r){
			var res = $.parseJSON(r);
			if(res.result=='error'){swal("Lỗi!",res.msg,"error");
			$('#text').text('Thực hiện lại');
			$('#btn-click').prop('disabled', false);
			return false
		}
		swal({title:'Đã đăng nhập',text: res.msg,type:'success',showCancelButton: false,confirmButtonColor:'#8CD4F5',confirmButtonText:'Tiếp tục',closeOnConfirm:false},function(){window.location.reload()});});
	}
	$('#btn-click').on('click',function(){
		click_login();
	});
	$(document).keyup(function(event){
	    if(event.keyCode == 13){
	        click_login();
	    }
	});
})(jQuery);