(function($){'use strict';
	$(document).keyup(function(event){
		if(event.keyCode == 13){
			$('#btn-click').click();
		}
	});
	function getPasswordStrength(pw){
	    var pwlength=(pw.length);
	    if(pwlength>5)pwlength=5;
	    var numnumeric=pw.replace(/[0-9]/g,"");
	    var numeric=(pw.length-numnumeric.length);
	    if(numeric>3)numeric=3;
	    var symbols=pw.replace(/\W/g,"");
	    var numsymbols=(pw.length-symbols.length);
	    if(numsymbols>3)numsymbols=3;
	    var numupper=pw.replace(/[A-Z]/g,"");
	    var upper=(pw.length-numupper.length);
	    if(upper>3)upper=3;
	    var pwstrength=((pwlength*10)-20)+(numeric*10)+(numsymbols*15)+(upper*10);
	    if(pwstrength<0){pwstrength=0}
	    if(pwstrength>100){pwstrength=100}
	    return pwstrength;
	}
	$('#btn-click').on('click',function(){
		var i = $.trim($('#code').val());
		var t = $.trim($('#password').val());
		var n = $.trim($('#password2').val());
		if(!t || !n || !i){
			swal('Không hợp lệ!','Hãy điền tất cả những thông tin cần thiết rồi thử lại','error');
			return false
		}
		if(t!=n){
			swal("Lỗi!","Mật khẩu bạn nhập 2 lần không giống nhau!","error");
			return false
		}
		if(getPasswordStrength(t)<40){
			swal("Lỗi!","Mật khẩu đơn giản quá sẽ khiến tài khoản của bạn không an toàn, hãy thử lại!","error");
			return false
		}
		$('#text').text('Đang thực hiện...');
		$('#btn-click').prop('disabled', true);
		$.post('/auth/ajaxnewpass',{code:i,password:t},function(r){
			var res = $.parseJSON(r);
			if(res.result=='error'){
				swal("Lỗi!", res.msg,"error");
				$('#text').text('Thực hiện lại');
				$('#btn-click').prop('disabled', false);
				return false
			}
			swal({title:'Thành công',text: res.msg,type:'success',showCancelButton: false,confirmButtonColor:'#8CD4F5',confirmButtonText:'Đăng nhập',closeOnConfirm:false},function(){window.location.replace('/')});});});})(jQuery);