(function($){'use strict';
	$(document).keyup(function(event){
		if(event.keyCode == 13){
			$('#btn-click').click();
		}
	});
	$('#btn-click').on('click',function(){
		var e = $.trim($('#email').val());
		if(!e || e.length < 8){
			swal('Không hợp lệ','Vui lòng điền địa chỉ email hợp lệ của bạn vào','error');
			return false
		}
		$('#btn-click').text('Đang kiểm tra...');
		$('#btn-click').prop('disabled', true);
		$.post('/auth/ajaxrspasswd',{email:e},function(r){
			var res = $.parseJSON(r);
			if(res.result=='error'){
				swal("Lỗi!", res.msg,"error");
				$('#btn-click').text('Thực hiện lại');
				$('#btn-click').prop('disabled', false);
				return false
			}
			swal({title:'Đã gửi!',text: res.msg,type:'success',showCancelButton: false,confirmButtonColor:'#8CD4F5',confirmButtonText:'Đặt lại mật khẩu',closeOnConfirm:false},function(){window.location.replace('/auth/set-new-password')});});});})(jQuery);