function list_cdn() {
	$(function() {
		$.getJSON("/cdn/ajax-list", function(data){
			if(data){
				if(data.result=='success'){
					var html = '', type, status;
					$.each(data.msg, function(){
						html += '<tr>';
						html += '<td><a href="/cdn/manager/'+this.id+'">'+this.name+'</a></td>';
						html += '<td><a href="/cdn/manager/'+this.id+'">'+this.plan_name+'</a></td>';
						if(this.type=='live'){
							type = 'Live Streaming';
						} else if(this.type=='vod'){
							type = 'VoD Streaming';
						} else {
							type = 'Http Download';
						}
						if(this.enable){
							status = '<span class="label label-success">Đang hoạt động</span>';
						} else {
							status = '<span class="label label-danger">Không hoạt động</span>';
						}
						html += '<td>'+type+'</td>';
						html += '<td>'+this.nextduedate+'</td>';
						html += '<td>'+status+'</td>';
						html += '</tr>';
					});
					$('#data').html(html);
				} else
					$('#data').html('<td colspan="6"><center><br /><a href="/cdn/new-cdn" class="btn btn-primary"> Tạo CDN đầu tiên</a></center></td>');
			} else
				$('#data').html('<td colspan="6"><center><i class="fa fa-warning"></i> Đã có lỗi xảy ra, vui lòng thử lại </center></td>');
		});
	});
}
$(function() {list_cdn()});