(function ($) {
	'use strict';
	function load_init() {
		var n = $('#id').val();
		var s = $('#hav').val();
		if(s==='no'){
			swal({title: "Chưa có Panel", text: "Bạn chưa có máy chủ nào chạy hệ điều hành hỗ trợ CloudONE Panel, bạn có thể tạo máy chủ mới với hệ điều hành \"CloudONE Panel\" để sử dụng chức năng này", type: "warning", showCancelButton: false, confirmButtonColor: "#4cc3d9",   confirmButtonText: "Tạo máy mới",   closeOnConfirm: false}, function(){window.location.replace('/instance/new-instance')});
			return false
		}
		if(typeof n === "undefined" || !n){
			$('#default').hide();
			$('#select-id').show();
			return false
		}
		return n;
	}
	function sbloading() {
		$('#loading').show();
		$('#loading').removeClass().addClass('alert alert-success');
		$('#loading').html('<i class="fa fa-refresh fa-spin"></i> vui lòng đợi chút...');
		$('.btn').attr('disabled', true);
		$("html, body").animate({ scrollTop: 0 }, "slow");
	}
	function sbresult(a, b) {
		$('#loading').removeClass().addClass('alert alert-'+a);
		$('#loading').html(b);
		$('.btn').attr('disabled', false);
		$('#loading').fadeTo(2000, 500).slideUp(1000, function(){
		    $('#loading').hide();
		});
	}

	var id = load_init();
	if(id){
		
	} else {
		$('#new_id').change(function(){
			id = $('#new_id').val();
			if(!id){
				return false
			}
			$('#id').val(id);
			$('#default').show();
			$('#select-id').hide();
		})
	}
})(jQuery);