function load_notify() {
	$(function() {
		var n = $('#instant_notify_n').text();
		var html, footer;
		$.getJSON("/account/ajax-notification?c="+n, function(data){
			if(data.result == 'success'){
				html = '<li class="notifications-header"><p class="text-muted small">Bạn có '+data.total+' tin nhắn mới</p></li><li><ul class="notifications-list">';
				footer = '';
				$.each(data.msg, function(){
					html += '<li><a href="/account/notification/'+this.id+'"><div class="pull-left mt2 mr15">';
					footer += '<li class="feed-item';
					if(this.type=='alert'){
						html += '<div class="circle-icon bg-danger"><i class="fa fa-warning"></i></div>';
						footer += ' inactive';
					} else if(this.type=='success'){
						html += '<div class="circle-icon bg-info"><i class="fa fa-check"></i></div>';
						footer += ' active';
					} else {
						html += '<div class="circle-icon bg-success"><i class="fa fa-comment"></i></div>';
						footer += ' active';
					}
					html += '</div><div class="overflow-hidden">';
					html += '<span>'+this.subject+'</span>';
                	html += '<span class="time">'+this.createdate+'</span>';
                	html += '</div></a></li>';
					footer += '"><span>'+this.subject+'</span><time datetime="'+this.createdate+'">'+this.createdate+'</time></li>';
				});
				html += '</ul></li><li class="notifications-footer"><a href="/account/notification">Xem toàn bộ tin nhắn</a></li>';
				$('#instant_notify').html(html);
				$('#instant_notify_footer').html(footer);
				$('#instant_notify_n').text(data.total);
			}
		});
	});
}
setInterval(load_notify, 20000);
$(function() {load_notify()});